/*********running a test step with groovy****/


def status = testRunner.runTestStepByName("Copy of Add")

def result = status.getStatus().toString()
log.info ("   >>>>     " + result)

if(result == "OK"){
	log.info("test passed")
	}
	else {
     log.info("test failed")		
		}

project = testRunner.testCase.testSuite.project;
tcase = project.testSuites["TestSuit1"].testCases["Add TestCase"];
tstep = tcase.getTestStepByName("Add")

def status = tstep.run(testRunner,context)
def result = status.getStatus().toString();
log.info(result)

def tCase = testRunner.testCase.testSuite.testCases["TestCase1"]
prop = new com.eviware.soapui.support.types.StringToObjectMap()

runner = tCase.run(prop,false)

log.info(runner.getStatus().toString())

def testCases = context.testCase.testSuite.getTestCaseList()

testCases.each{
	log.info(it.name)
	}

/************Groovy script to run a test suit**************/
